import { useState, useEffect } from "react";
import { Link } from "react-scroll";
function Navbar() {
    const [navActive, setNavActive] = useState(false);
  
    const toggleNav = () => {
      setNavActive(!navActive);
    };
  
    const closeMenu = () => {
      setNavActive(false);
    };
  
    useEffect(() => {
      const handleResize = () => {
        if (window.innerWidth <= 500) {
          closeMenu();
        }
      };
  
      window.addEventListener("resize", handleResize);
  
      return () => {
        window.removeEventListener("resize", handleResize);
      };
    }, []);
  
    useEffect(() => {
      if (window.innerWidth <= 1200) {
        closeMenu();
      }
    }, []);
  
    return (
      <nav className={`nav ${navActive ? "active" : ""}`}>
        <div>
          <img src="/image/Panhawat logo.png" alt="Logo" />
        </div>
        <a className={`nav_hambuger ${navActive ?"active" : "" }`}onClick={toggleNav}>

       
        <span className="nav_hanberger-line"></span>
        <span className="nav_hanberger-line"></span>
        <span className="nav_hanberger-line"></span>
        </a>
        <div className={`navbar--items ${navActive ? "active" :"" }`}>
          <ul>
            <li>
              <Link onClick={closeMenu}activeClass="navBar--active--content"spy={true}smooth={true}
              offset={-70}
              duration={500}
              to="heroSection"
              className="navbar--content"
              >
                Home
                </Link>
            </li>
            <li>
              <Link onClick={closeMenu}activeClass="navBar--active--content"spy={true}smooth={true}
              offset={-70}
              duration={500}
              to="MyPortfolio"
              className="navbar--content"
              >
                Portfolio
                </Link>
            </li>
            <li>
              <Link onClick={closeMenu}activeClass="navBar--active--content"spy={true}smooth={true}
              offset={-70}
              duration={500}
              to="AboutMe"
              className="navbar--content"
              >
               AboutMe
                </Link>
            </li>
            <li>
              <Link onClick={closeMenu}activeClass="navBar--active--content"spy={true}smooth={true}
              offset={-70}
              duration={500}
              to="MyPortfolio"
              className="navbar--content"
              >
                Portfolio
                </Link>
            </li>
          </ul>
        </div>

      </nav>
    );
  }
  
  export default Navbar;
