import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGlobe, faSearch } from "@fortawesome/free-solid-svg-icons"; // Import FontAwesome icons for search

function Navbar() {
  const [navActive, setNavActive] = useState(false);
  const [searchQuery, setSearchQuery] = useState(""); // State for the search query
  const [isDarkMode, setIsDarkMode] = useState(false); // State for dark mode

  const toggleNav = () => {
    setNavActive(!navActive);
  };

  const closeMenu = () => {
    setNavActive(false);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 500) {
        closeMenu();
      }
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    if (window.innerWidth <= 1200) {
      closeMenu();
    }
  }, []);

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Handle the search query as needed, e.g., navigate to search results.
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    // Add logic to update dark mode in local storage or a global state manager (e.g., Redux).
  };

  return (
    <nav className={`navbar ${navActive ? "active" : ""} ${isDarkMode ? "dark-mode" : ""}`}>
      <div className="logo">
        <img src="/image/Panhawat logo.png" alt="Logo" />
      </div>
      <a
        href="#"
        className={`nav__hamburger ${navActive ? "active" : ""}`}
        onClick={toggleNav}
      >
        <span className="nav__hamburger__line"></span>
        <span className="nav__hamburger__line"></span>
        <span className="nav__hamburger__line"></span>
      </a>
      <div className={`navbar--items ${navActive ? "active" : ""}`}>
        {/* Your menu items */}
      </div>

      <div className="dark-mode-toggle">
        <button onClick={toggleDarkMode}>
          {isDarkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>

      <Link
        onClick={closeMenu}
        activeClass="navbar--active-content"
        spy={true}
        smooth={true}
        offset={-70}
        duration={500}
        to="Contact"
        className="btn btn-outline-primary"
      >
        Contact Me
      </Link>
    </nav>
  );
}

export default Navbar;
